module.exports.revert = function (text, fontTypes) {
  fontTypes.forEach(type => {
    text = origin(text, type);
  });
  return text;
};

function origin(text, type) {
  try {
    const reverse = Object.keys(type).reduce((newObject, key) => {
      newObject[type[key]] = key;
      return newObject;
    }, {});

    for (const [bold, regular] of Object.entries(reverse)) {
      text = text.replace(new RegExp(bold, "g"), regular);
    }
    return text;
  } catch (err) {
    console.error("An error occured while reverting the text origin:", err);
  }
}